GtkWidget*
create_about1 (void)
{
  const gchar *authors[] = {
    "Daniel Balster <dbalster@psxdev.de>",
    NULL
  };
  GtkWidget *about1;

  about1 = gnome_about_new ("CDMaker", VERSION,
                        _("Copyright (C) 2000 by Daniel Balster"),
                        authors,
                        _("A simple frontend for CDRDAO and MKISOFS "),
                        "psxcdmaker/logo.png");
  gtk_object_set_data (GTK_OBJECT (about1), "about1", about1);
  gtk_window_set_modal (GTK_WINDOW (about1), TRUE);

  return about1;
}

